import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../AuthContext";

const ProfileForm = ({ user, new: isNew }) => {
    const { userId, token } = useAuth();
    const [profile, setProfile] = useState({
        user_id: userId,
        bio: user.bio || "",
        major: user.major || "",
        year: user.year || "",
        dateOfBirth: user.date_of_birth || "2025-01-01",
    });
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        setProfile({
            user_id: userId,
            bio: user.bio || "",
        major: user.major || "",
        year: user.year || "",
        dateOfBirth: user.date_of_birth || "2025-01-01",
        });
    }, [user]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProfile((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };
    const createProfile = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`/users/profiles`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify(profile),
            });

            if (!response.ok) {
                throw new Error("Failed to create profile");
            }
            const newProfile = await response.json();
            console.log(newProfile);
            navigate(`/profiles`);
        } catch (err) {
            setError(err.message);
        }
    };
    const editProfile = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`/profiles/${user.id}`, {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify(profile),
            });

            if (!response.ok) {
                throw new Error("Failed to update profile");
            }
            navigate(`/profiles`);
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="ProfileForm">
            <form>
                <div className="formRow">
                    <label htmlFor="major">Major:</label>
                    <input
                        type="text"
                        id="major"
                        name="major"
                        value={profile.major}
                        onChange={handleInputChange}
                    />
                </div>

                <div className="formRow">
                    <label htmlFor="year">Graduation Year:</label>
                    <input
                        type="text"
                        id="year"
                        name="year"
                        value={profile.year}
                        onChange={handleInputChange}
                    />
                </div>

                <div className="formRow">
                    <label htmlFor="dateOfBirth">Date of Birth:</label>
                    <input
                        type="date"
                        id="dateOfBirth"
                        name="dateOfBirth"
                        value={profile.dateOfBirth}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="formRow">
                    <label htmlFor="bio">Bio:</label>
                    <textarea
                        id="bio"
                        name="bio"
                        value={profile.bio}
                        onChange={handleInputChange}
                    />
                </div>
                <button type="submit" onClick={isNew? createProfile : editProfile}>{isNew ? "Create Profile" : "Save Changes"}</button>
            </form>

            {error && <p className="error">{error}</p>}
        </div>
    );
};

export default ProfileForm;
